import React, {useState} from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import { Menu, Input, Row, Col } from 'antd';
import styled from 'styled-components';

import UserProfile from './UserProfile';
import LoginForm from './LoginForm';

const enterButton = styled(Input.Search)`
  vertical-align: middle
  `;

const AppLayout = ({children}) => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <>
    <div>
        <Menu mode="horizontal">
          <Menu.Item>
            <Link href="/"><a>Node Bird</a></Link>
          </Menu.Item>
          <Menu.Item>
            <Link href="/profile"><a>Profile</a></Link>
          </Menu.Item>
          <Menu.Item>
            <Input.Search enterButton/>
          </Menu.Item>
          <Menu.Item>
            <Link href="/signup"><a>Sign Up</a></Link>
          </Menu.Item>
        </Menu>
        <Row gutter={8}> 
          <Col xs={24} md={6}>
            {isLoggedIn ? <UserProfile /> : <LoginForm  setIsLoggedIn={setIsLoggedIn}/>}  
          </Col>
          <Col xs={24} md={12}>
           {children}
          </Col>
          <Col xs={24} md={6}>
            <a href='https://www.github.com/Na-David' target="_blank" rel="noreferrer noopener">GitHub</a>
          </Col>
        </Row>
    </div>
    </>
  )
}

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
}

export default AppLayout
